targets.query:
from(bucket: "xdc_metrics")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "consensus_missed_rounds" and r.chainId == "51")
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  // Group by unique identifiers for a missed round
  |> group(columns: ["round", "block_number", "expected_miner", "actual_miner"])
  // Take most recent record for each group
  |> sort(columns: ["_time"], desc: true)
  |> limit(n: 1)
  // Ungroup for final sorting
  |> group()
  |> sort(columns: ["block_number"], desc: true)
  |> limit(n: 100)
